SELECT ap.ref,ap.status,ot.description,oo.description,ap.type,g.description 'Gerencia',gs.description 'Gerencia 2da' FROM `ArrangementProgram` ap LEFT JOIN seip_objetive ot ON ot.id = ap.`tactical_objective_id` LEFT JOIN seip_objetive oo ON oo.id = ap.`operational_objective_id` LEFT JOIN seip_c_gerencia g ON g.id = ot.fk_gerencia LEFT JOIN seip_c_gerencia_second gs ON gs.id = oo.fk_gerencia_second ORDER BY ap.status,g.description,gs.description